import React from 'react'
import AnimeCard from '../AnimeCard';




function MainContent(props) {
	let validSearch;

	//animelist.append(mainHead)
	const validSearchFunc = (query) => {
		if (query > 3) {
			validSearch = true;
			props.SetSearch(query.target.value)
		} else {
			validSearch = false;
		}
	}
	return (
		<main>
			<div className="main-head">
				<form
					className="search-box"
					onSubmit={props.HandleSearch}>
					<input
						type="search"
						placeholder="Search for an anime..."
						required
						value={props.search}
						onChange={e => {
							validSearchFunc(e.target.value);
						}
						}
					/>
				</form>

			</div>
			{validSearch ?
				() => {
					return (
						<div className="anime-list">
							{props.animeList.map(anime => {
								return (<AnimeCard
									anime={anime}
									key={anime.mal_id} />
								)
							})}
						</div>
					)
				} :
				() => {
					return (
						<div className="anime-list">
							<div className="blank-search">
								There's nothing here..
							</div>
						</div>
					)
				}
			}
		</main>
	)
}

export default MainContent
