import React, { useState } from 'react'

const panels = document.querySelectorAll('.panel')

function toggleOpen() {
    console.log('toggleOpen()')
    this.classList.toggle('open')
}

function toggleActive(e) {
    if (e.propertyName.includes('flex')) { // 'includes' from js.
        this.classList.toggle('open-active')
    }
}


function PanelCard({ anime, num }) {
    const [isOpen, setOpen] = useState("false");
    const [isActive, setActive] = useState("false");

    const handleActiveToggle = () => setActive(!isActive);
    const handleOpenToggle = () => setOpen(!isOpen);

    let className = "panels_panel-" + num + (isOpen ? " open" : ".")
    return (
        <div className={className} onMouseOver={toggleOpen} onTransitionEnd={toggleActive}>
            <a
                href={anime.url}
                target="_blank"
                rel="noreferrer">
                <figure>
                    <img src={anime.image_url} alt="anime image" />
                </figure>
            </a >
        </div >
    )
}

export default PanelCard
