import React, { useState } from 'react'




function PanelCard({ anime, num }) {
    const [isOpen, setOpen] = useState("false");

    const handleOpenToggle = () => setOpen(!isOpen);

    let className = "panels__panel-" + num +
        `
        ${isOpen ? " open" : ""}
        `
    return (
        <div className={className} onMouseEnter={handleOpenToggle} >
            <a
                href={anime.url}
                target="_blank"
                rel="noreferrer">
                <figure>
                    <img src={anime.image_url} alt="anime image" />
                </figure>
            </a >
        </div >
    )
}

export default PanelCard
