import React, { useState } from 'react'
import AnimeCard from '../AnimeCard';
import ReactDOM from 'react-dom';



function MainContent(props) {
	const [searchChars, setSearchChars] = useState([]);
	const HandleNumSearchChars = e => {
		setSearchChars([]);// reset searchChars
		for (let i = 0; i < e.target.value.length; i++) {
			setSearchChars[i] = 1; // using 1 as a placeholder, just need a placeholder in the array.
		}
	}
	return (
		<main>
			<div className="main-head">
				<form
					className="search-box"
					onSubmit={props.HandleSearch}>
					<input
						type="search"
						placeholder="Search for an anime..."
						required
						value={props.search}
						onChange={e => {
							HandleNumSearchChars(e)
							if (e.target.value.length > 3) {
								document.querySelector(".blank-search").style.display = "none";
								props.SetSearch(e.target.value);
							}
							else {
								props.SetSearch(e.target.value);
								ReactDOM.render(
									<div className="blank-search">There's nothing here..</div>,
									document.querySelector(".anime-list")
								)
							}
						}
						}
					/>
				</form>

			</div>
			<div className="anime-list">
				{/* if searchChars.length is 0, load blank search.
				if searchChars.length is not 0, then load anime cards.
			 */}
				{searchChars.map(anime => {
					return (<AnimeCard
						anime={anime}
						key={anime.mal_id} />
					)
				})}

			</div>
		</main>
	)
}

export default MainContent
