import React from 'react';
import PanelCard from '../PanelCard/PanelCard';

const panels = document.querySelectorAll('.panel')

function toggleOpen() {
    this.classList.toggle('open')
}

function toggleActive(e) {
    if (e.propertyName.includes('flex')) { // 'includes' from js.
        this.classList.toggle('open-active')
    }
}

panels.forEach((panel) => panel.addEventListener('click', toggleOpen))
panels.forEach((panel) => panel.addEventListener('transitionend', toggleActive)) // widens the flex panel.

function Gallery(props) { // ! pass down props.

    const displayPanels = () => {
        return props.animeList.map((anime, i = 0) => {
            return <PanelCard
                num={i++}
                anime={anime}
                key={anime.mal_id} />
        })
    }
    return (
        <div className="gallery-container">
            <div className="panels">
                {displayPanels()}
                <div className="panel panels__panel-3">
                    <p>Experience</p>
                    <p>It</p>
                    <p>Today</p>
                </div>
            </div>
        </div>

    )
}

export default Gallery;
